import React, { Fragment } from 'react';
import NavBar from '../components/navBar/NavBar';
import './Main.css';
import ExperienceBox from '../components/experienceBox/ExperienceBox';
import SkillBox from '../components/skillBox/SkillBox';
import ProjectBox from '../components/projectBox/ProjectBox';
import Fade from 'react-reveal/Fade';

export default class Main extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            scrollHeight: 0,
            buttonText: 'Send',
            name: "",
            email: "",
            message: "",
            shouldSubmit: false,
        }

        this.name = React.createRef();
        this.email = React.createRef();
        this.message = React.createRef();
    }

    componentDidMount() {
        document.querySelector('#content').addEventListener('scroll', this.updateScroll);
    };

    updateScroll = event => {
        let scrollTop = event.srcElement.scrollTop;

        this.setState({
            scrollHeight: scrollTop,
        });
    }

    onChange = (field, event) => {
        if (field === "name") {
            this.setState({
                name: event.target.value,
            })
            this.name.current.style.border = "#252525";
        } else if (field === "email") {
            this.setState({
                email: event.target.value,
            })
            this.name.current.style.border = "#252525";
        } else {
            this.setState({
                message: event.target.value,
            })
            this.name.current.style.border = "#252525";
        }
    }

    updateBorder = index => {
    }

    validateEmail = () => {
     if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(this.state.email))
      {
        return true;
      }
        alert("You have entered an invalid email address!")
        return false;
        this.name.current.style.border = "red";
    }

    validateName = () => {
        if (this.state.name) {
            return true;
        } else {
            return false;
            this.email.current.style.border = "red";
        }
    }

    validateMessage = () => {
        if (this.state.message) {
            return true;
        } else {
            return false;
            this.message.current.style.border = "red";
        }
    }

    validateSubmit = () => {
        if (this.validateEmail && this.validateName && this.validateMessage) {
            this.setState({
                shouldSubmit: true,
            })
        }
    }

    sendEmail = (event) => {
        if (this.validateSubmit()) {
            this.setState({
                buttonText: 'Sending...',
            })

            const templateId = 'portfolio_contact';

            this.sendFeedback(templateId, {message_html: this.state.message, from_name: this.state.name, reply_to: this.state.email})    
        }


        // this.setState({
        //     buttonText: 'Sent!',
        // })

        // setTimeout(() => {
        //     this.setState({
        //         buttonText: 'Send',
        //     })
        // }, 2000)
    }

    sendFeedback (templateId, variables) {
        window.emailjs.send(
          'gmail', templateId,
          variables
          ).then(res => {
            console.log('Email successfully sent!')
          })
          // Handle errors here however you like, or use a React error boundary
          .catch(err => console.error('Oh well, you failed. Here some thoughts on the error that occured:', err))
      }

    render() {
        return (
            <Fragment>
                <NavBar top={this.state.scrollHeight}/>
                <article id="content">
                    <section id="banner">
                        <div id="leftBanner">
                            <h1 id="name">Max De George</h1>
                            <h2 className="job">Web Developer</h2>
                            <h2 className="job">& Game Programmer</h2>
                        </div>
                        <div id="rightBanner">
                            <img src={"https://via.placeholder.com/300"} alt="Max De George"/>
                        </div>
                    </section>
                    <section id="about">
                        <Fade bottom>
