import React, { Fragment } from 'react';
import NavBar from '../components/navBar/NavBar';
import './Main.css';

export default class Main extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            scrollHeight: 0,
        }
    }

    componentDidMount() {
        document.querySelector('#content').addEventListener('scroll', this.updateScroll);
    };

    updateScroll = event => {
        let scrollTop = event.srcElement.scrollTop;

        this.setState({
            scrollHeight: scrollTop,
        });
    }

    render() {
        return (
            <Fragment>
                <NavBar top={this.state.scrollHeight}/>
                <article id="content">
                    <section id="banner">
                        <div id="leftBanner">
                            <h1 id="name">Max De George</h1>
                            <h2 className="job">Web Developer</h2>
                            <h2 className="job">& Game Designer</h2>
                        </div>
                        <div id="rightBanner">
                            <img src={"https://via.placeholder.com/300"} alt="Max De George"/>
                        </div>
                    </section>
                    <section id="about">
                        <div className="sectionHeader">About</div>
                        <p id="bio">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt 
                            ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco 
                            laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in 
                            voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat 
                            non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </p>
                        <div className="divider"></div>
                    </section>
                    <section id="experience">
                        <div className="sectionHeader">Experience</div>
                        <div className="divider"></div>
                    </section>
                    <section id="skills">
                        <div className="sectionHeader">Skills</div>
                        <div className="divider"></div>
                    </section>
                    <section id="projects">
                        <div className="sectionHeader">Projects</div>
                        <div className="divider"></div>
                    </section>
                    <section id="contact">
                        <div className="sectionHeader">Contact</div>
                    </section>
                </article>
            </Fragment>
        );
    }
}
